import React from 'react';

const Rate = ({ rating, setRatingSearch }) => {
  const stars = (x) => {
    let starsArray = [];
    for (let i = 1; i <= 5; i++) {
      if (i <= x) {
        starsArray.push(
          <span key={i} onClick={() => setRatingSearch(i)}>
            ★
          </span>
        );
      } else {
        starsArray.push(
          <span key={i} onClick={() => setRatingSearch(i)}>
            ☆
          </span>
        );
      }
    }
    return starsArray;
  };
  return <div>{stars(rating)}</div>;
};

Rate.defaultProps = {
  setRatingSearch: () => {},
  rating: 1,
};

export default Rate;
